import "../QuestionForm.css";
import { useState } from "react";
import FormFooter from "../FormFooter";

function Question3Options(props) {
  const data = props.data;
  console.log(data);
  const childs = data.Child;

  function option1ClickedHandler(event) {
    event.preventDefault();
    props.nextEvent(childs[0].Child);
  }
  function option2ClickedHandler(event) {
    event.preventDefault();
    props.nextEvent(childs[1].Child);
  }
  function option3ClickedHandler(event) {
    event.preventDefault();
    props.nextEvent(childs[1].Child);
  }

  return (
    <body>
      <div class="header">
        <h1>{data.Question}</h1>
      </div>

      <div class="row">
        <div class="col-s-12 col-s-12">
          <div class="question">
            <h1 class="question">{data.QuestionExplanation}</h1>
          </div>

          <div class="row">
            <div class="col-4 col-s-4 buttons">
              <button
                id="close-image"
                class="big-button"
                onClick={option1ClickedHandler}
              >
                <img src={require("../" + childs[0].OptionImage).default} />
              </button>
              <div class="row">
                <h1 class="option">{childs[0].Option}</h1>
              </div>
            </div>
            <div class="col-4 col-s-4 buttons">
              <button
                id="close-image"
                class="big-button"
                onClick={option2ClickedHandler}
              >
                <img src={require("../" + childs[1].OptionImage).default} />
              </button>
              <div class="row">
                <h1 class="option">{childs[1].Option}</h1>
              </div>
            </div>
            <div class="col-4 col-s-4 buttons">
              <button
                id="close-image"
                class="big-button"
                onClick={option3ClickedHandler}
              >
                <img src={require("../" + childs[2].OptionImage).default} />
              </button>
              <div class="row">
                <h1 class="option">{childs[2].Option}</h1>
              </div>
            </div>
          </div>
        </div>
      </div>
      <FormFooter></FormFooter>
    </body>
  );
}
export default Question3Options;
